SELECT bae.title catalogs_title, bae.media_number catalogs_media_number, bae.book_id catalogs_book_id, bae.id catalogs_audio_episode_id FROM book_audio_episode bae WHERE bae.deleted = 0
AND bae.status = #{status}
AND bae.book_id = #{bookId} ORDER BY bae.media_number


and bae.book_id = #{bookId}
ORDER BY bae.update_time DESC
LIMIT 0,1;
